actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entity = {};

    vm.editEntityFormGroup = vm.fb.group({
        firmwareId: [null]
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveEntity().subscribe(
            function () {
                // widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };


    function getEntityInfo() {
        entityService.getEntity(entityId.entityType, entityId.id).subscribe(
            function (data) {
                vm.entity = data;
                vm.editEntityFormGroup.patchValue({
                    firmwareId: vm.entity.firmwareId
                }, {emitEvent: false});
            }
        );
    }

    function saveEntity() {
        const formValues = vm.editEntityFormGroup.value;
        vm.entity.firmwareId = formValues.firmwareId;
        return deviceService.saveDevice(vm.entity);
    }
}

let $injector = widgetContext.$scope.$injector;
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let otaPackageService = $injector.get(widgetContext.servicesMap.get('otaPackageService'));
let deviceProfileService = $injector.get(widgetContext.servicesMap.get('deviceProfileService'));

getDeviceFirmware();

function getDeviceFirmware() {
    var entityIdValue = entityId.id;
    var data = widgetContext.data.find((el) => el.datasource.entityId === entityIdValue && el.dataKey.name === 'fw_url');
    var url = data.data[0][1];
    if (url === '') {
        entityService.getEntity(entityId.entityType, entityId.id).subscribe(
            function (data) {
                if (data.firmwareId !== null) {
                    otaPackageService.downloadOtaPackage(data.firmwareId.id).subscribe();    
                } else {
                    deviceProfileService.getDeviceProfile(data.deviceProfileId.id).subscribe(
                        function (deviceProfile) {
                            if (deviceProfile.firmwareId !== null) {
                                otaPackageService.downloadOtaPackage(deviceProfile.firmwareId.id).subscribe();
                            } else {
                                widgetContext.showToast('warn', 'Device ' + entityName +' has not firmware set.', 2000, 'top');
                            }
                    });
                }
            }
        );
    } else {
        widgetContext.showToast('warn', 'Device ' + entityName +' has not firmware set.', 2000, 'top');
    }
}

function copyToClipboard(text) {
    if (window.clipboardData && window.clipboardData.setData) {
        return window.clipboardData.setData("Text", text);

    }
    else if (document.queryCommandSupported && document.queryCommandSupported("copy")) {
        var textarea = document.createElement("textarea");
        textarea.textContent = text;
        textarea.style.position = "fixed";
        document.body.appendChild(textarea);
        textarea.select();
        try {
            return document.execCommand("copy");
        }
        catch (ex) {
            console.warn("Copy to clipboard failed.", ex);
            return false;
        }
        document.body.removeChild(textarea);
    }
}
var entityIdValue = entityId.id;
var data = widgetContext.data.find((el) => el.datasource.entityId === entityIdValue && el.dataKey.name === 'fw_checksum');
var checksum = data.data[0][1];
if (checksum !== '') {
    copyToClipboard(checksum);
    widgetContext.showSuccessToast('Firmware checksum has been copied to clipboard', 2000, 'top');
} else {
    data = widgetContext.data.find((el) => el.datasource.entityId === entityIdValue && el.dataKey.name === 'fw_url');
    var url = data.data[0][1];
    if (url !== '') {
        copyToClipboard(url);
        widgetContext.showSuccessToast('Firmware direct URL has been copied to clipboard', 2000, 'top');
    } else {
        widgetContext.showToast('warn', 'Device ' + entityName +' has not firmware set.', 2000, 'top');
    }
}

