let $injector = widgetContext.$scope.$injector;
let $mdDialog = $injector.get('$mdDialog'),
    $document = $injector.get('$document'),
    $q = $injector.get('$q'),
    $rootScope = $injector.get('$rootScope'),
    types = $injector.get('types'),
    deviceService = $injector.get('deviceService'),
    attributeService = $injector.get('attributeService');

openAddDeviceDialog();

function openAddDeviceDialog() {
    $mdDialog.show({
        controller: ['$scope', '$mdDialog',
            AddDeviceDialogController
        ],
        controllerAs: 'vm',
        template: htmlTemplate,
        parent: angular.element($document[0].body),
        targetEvent: $event,
        multiple: true,
        clickOutsideToClose: false
    });
}

function AddDeviceDialogController($scope, $mdDialog) {
    let vm = this;
    vm.types = types;
    vm.attributes = {};
    vm.deviceType = "gateway";

    vm.cancel = () => {
        $mdDialog.hide();
    };

    vm.save = () => {
        vm.loading = true;
        $scope.addDeviceForm.$setPristine();
        let device = {
            additionalInfo: {gateway: true},
            name: vm.deviceName,
            type: vm.deviceType,
            label: vm.deviceLabel
        };
        deviceService.saveDevice(device).then(
            (device) => {
                saveAttributes(device.id).then(
                    () => {
                        vm.loading = false;
                        updateAliasData();
                        $mdDialog.hide();
                    }
                );
            },
            () => {
                vm.loading = false;
            }
        );
    };

    function saveAttributes(entityId) {
        let attributesArray = [];
        for (let key in vm.attributes) {
            attributesArray.push({
                key: key,
                value: vm.attributes[key]
            });
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(
                entityId.entityType, entityId.id,
                "SERVER_SCOPE", attributesArray);
        } else {
            return $q.when([]);
        }
    }

    function updateAliasData() {
        let aliasIds = [];
        for (let id in widgetContext.aliasController
                .resolvedAliases) {
            aliasIds.push(id);
        }
        let tasks = [];
        aliasIds.forEach((aliasId) => {
            widgetContext.aliasController
                .setAliasUnresolved(aliasId);
            tasks.push(widgetContext.aliasController
                .getAliasInfo(aliasId));
        });
        $q.all(tasks).then(() => {
            $rootScope.$broadcast(
                'widgetForceReInit');
        });
    }
}

