    $rootScope = $injector.get('$rootScope'),
    $q = $injector.get('$q');

openDeleteDeviceDialog();

function openDeleteDeviceDialog() {
    let title = "Are you sure you want to delete the device " + entityName +  "?";
    let content = "Be careful, after the confirmation, the device and all related data will become unrecoverable!";
    let confirm = $mdDialog.confirm()
        .targetEvent($event)
        .title(title)
        .htmlContent(content)
        .ariaLabel(title)
        .cancel('Cancel')
        .ok('Delete');
    $mdDialog.show(confirm).then(() => {
        deleteDevice();
    })
}

function deleteDevice() {
    deviceService.deleteDevice(entityId.id).then(
        () => {
            updateAliasData();
        }
    );
}

function updateAliasData() {
    let aliasIds = [];
    for (let id in widgetContext.aliasController.resolvedAliases) {
        aliasIds.push(id);
    }
    let tasks = [];
    aliasIds.forEach((aliasId) => {
        widgetContext.aliasController.setAliasUnresolved(aliasId);
        tasks.push(widgetContext.aliasController.getAliasInfo(aliasId));
    });
    $q.all(tasks).then(() => {
        $rootScope.$broadcast('entityAliasesChanged', aliasIds);
    });
}

