headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;

    vm.addEntityFormGroup = vm.fb.group({
        entityName: ['', [vm.validators.required]],
        attributes: vm.fb.group({
            alarmTemperature: [false],
            thresholdTemperature: [{value: null, disabled: true}],
            alarmHumidity: [false],
            thresholdHumidity: [{value: null, disabled: true}]
        })
    });

    vm.addEntityFormGroup.get('attributes').get('alarmTemperature').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.addEntityFormGroup.get('attributes').get('thresholdTemperature').enable();
        } else {
            vm.addEntityFormGroup.get('attributes').get('thresholdTemperature').disable();
        }
    });

    vm.addEntityFormGroup.get('attributes').get('alarmHumidity').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.addEntityFormGroup.get('attributes').get('thresholdHumidity').enable();
        } else {
            vm.addEntityFormGroup.get('attributes').get('thresholdHumidity').disable();
        }
    });

    vm.save = function() {
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (entity) {
                saveAttributes(entity.id).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            name: formValues.entityName,
            type: "thermostat"
        };
        return deviceService.saveDevice(entity);
    }

    function saveAttributes(entityId) {
        let attributes = vm.addEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if(attributes[key] !== null) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

