let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityId = entityId;
    vm.entityName = entityName;
    vm.attributes = {};

    vm.editEntityFormGroup = vm.fb.group({
        entityName: [''],
        attributes: vm.fb.group({
            alarmTemperature: [false],
            thresholdTemperature: [{value: null, disabled: true}],
            alarmHumidity: [false],
            thresholdHumidity: [{value: null, disabled: true}]
        })
    });

    vm.editEntityFormGroup.get('attributes').get('alarmTemperature').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.editEntityFormGroup.get('attributes').get('thresholdTemperature').enable();
        } else {
            vm.editEntityFormGroup.get('attributes').get('thresholdTemperature').disable();
        }
    });

    vm.editEntityFormGroup.get('attributes').get('alarmHumidity').valueChanges
    .subscribe(activate => {
        if (activate) {
            vm.editEntityFormGroup.get('attributes').get('thresholdHumidity').enable();
        } else {
            vm.editEntityFormGroup.get('attributes').get('thresholdHumidity').disable();
        }
    });


    getEntityInfo();


    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveAttributes(entityId).subscribe(
            function () {
                vm.dialogRef.close(null);
            }
        );
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function getEntityAttributes(attributes) {
        for (var i = 0; i < attributes.length; i++) {
            vm.attributes[attributes[i].key] = attributes[i].value;
        }
    }

    function getEntityInfo() {
        attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE').subscribe(
            function (attributes) {
                getEntityAttributes(attributes);
                vm.editEntityFormGroup.patchValue({
                    entityName: vm.entityName,
                    attributes: vm.attributes
                });
                // if(vm.attributes.alarmTemperature) {
                //     vm.editEntityFormGroup.get('attributes').get('thresholdTemperature').enable();
                // }
                // if(vm.attributes.alarmHumidity) {
                //     vm.editEntityFormGroup.get('attributes').get('thresholdHumidity').enable();
                // }
            }
        );
    }

    function saveAttributes(entityId) {
        let attributes = vm.editEntityFormGroup.get('attributes').value;
        let attributesArray = [];
        for (let key in attributes) {
            if (attributes[key] !== vm.attributes[key]) {
                attributesArray.push({key: key, value: attributes[key]});
            }
        }
        if (attributesArray.length > 0) {
            return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", attributesArray);
        } else {
            return widgetContext.rxjs.of([]);
        }
    }
}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = 'Delete thermostat "' +  entityName + '"';
    let content = 'Are you sure you want to delete the thermostat "' +
        entityName + '"?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
}

function deleteEntity() {
    deviceService.deleteDevice(entityId.id).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the thermostat. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

