 flowAnimationSpeed
} = ctx.values;
const {
 flowAnimationWidth: lineWidth,
 flowAnimationColor: lineColor,
 flowStyleDash: dashWidth,
 flowStyleGap: dashGap,
 flowDashCap: dashCap
} = ctx.properties;
const line = ctx.tags.line[0].attr('d');
const lineReversed = 'M 100,200 L 100,125 Q 100,100 75,100 L 0, 100';
const animation = ctx.tags.animationGroup[0];
const duration = 1 / flowAnimationSpeed;

let animateFlow = ctx.api.connectorAnimation(animation);

if (flowAnimation) {
 if (!animateFlow) {
 animateFlow = ctx.api.connectorAnimate(animation, line, lineReversed).flowAppearance(lineWidth, lineColor, dashCap, dashWidth, dashGap).duration(duration).direction(flowDirection).play();
 } else {
 animateFlow.duration(duration).direction(flowDirection).play();
 }
} else {
 if (animateFlow) {
 animateFlow.finish();
 }
}
