descriptor.controllerScript:
self.onInit = function() {
}

self.onDataUpdated = function() {
    self.ctx.$scope.alarmsTableWidget.onDataUpdated();
}

self.actionSources = function() {
    return {
        'actionCellButton': {
            name: 'widget-action.action-cell-button',
            multiple: true
        },
        'rowClick': {
            name: 'widget-action.row-click',
            multiple: false
        }
    };
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "AlarmTableSettings",
        "properties": {
            "alarmsTitle": {
                "title": "Alarms table title",
                "type": "string",
                "default": ""
            },
            "enableSelection": {
                "title": "Enable alarms selection",
                "type": "boolean",
                "default": true
            },
            "enableSearch": {
                "title": "Enable alarms search",
                "type": "boolean",
                "default": true
            },
            "enableSelectColumnDisplay": {
                "title": "Enable select columns to display",
                "type": "boolean",
                "default": true
            },
            "enableStatusFilter": {
                "title": "Enable alarm status filter",
                "type": "boolean",
                "default": true
            },
            "displayDetails": {
                "title": "Display alarm details",
                "type": "boolean",
                "default": true
            },
            "allowAcknowledgment": {
                "title": "Allow alarms acknowledgment",
                "type": "boolean",
                "default": true
            },
            "allowClear": {
                "title": "Allow alarms clear",
                "type": "boolean",
                "default": true
            },
            "displayPagination": {
                "title": "Display pagination",
                "type": "boolean",
                "default": true
            },
            "defaultPageSize": {
                "title": "Default page size",
                "type": "number",
                "default": 10
            },
            "defaultSortOrder": {
                "title": "Default sort order",
                "type": "string",
                "default": "-createdTime"
            }
        },
        "required": []
    },
    "form": [
        "alarmsTitle",
        "enableSelection",
        "enableSearch",
        "enableSelectColumnDisplay",
        "enableStatusFilter",
        "displayDetails",
        "allowAcknowledgment",
        "allowClear",
        "displayPagination",
        "defaultPageSize",
        "defaultSortOrder"
    ]
}

