                        },
                        "label": {
                            "title": "Label",
                            "type": "string"
                        },
                        "row": {
                            "title": "Row",
                            "type": "number"
                        },
                        "col": {
                            "title": "Column",
                            "type": "number"
                        }
                    },
                    "required": ["pin", "label", "row", "col"]
                }
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "switchPanelBackgroundColor": {
                "title": "Switches panel background color",
                "type": "string",
                "default": "#008a00"
            },
            "gpioStatusRequest": {
                "title": "GPIO status request",
                "type": "object",
                 "properties": {
                    "method": {
                        "title": "Method name",
                        "type": "string",
                        "default": "getGpioStatus"
                    },
                    "paramsBody": {
                      "title": "Method body",
                      "type": "string",
                      "default": "{}"
                    }
                },
                "required": ["method", "paramsBody"]
            },
            "gpioStatusChangeRequest": {
                "title": "GPIO status change request",
                "type": "object",
                 "properties": {
                    "method": {
                        "title": "Method name",
                        "type": "string",
                        "default": "setGpioStatus"
                    },
                    "paramsBody": {
                      "title": "Method body",
                      "type": "string",
                      "default": "{\n   \"pin\": \"{$pin}\",\n   \"enabled\": \"{$enabled}\"\n}"
                    }
                },
                "required": ["method", "paramsBody"]
            },
            "parseGpioStatusFunction": {
                "title": "Parse gpio status function",
                "type": "string",
                "default": "return body[pin] === true;"
            } 
        },
        "required": ["gpioList", 
                     "requestTimeout",
                     "switchPanelBackgroundColor",
                     "gpioStatusRequest",
                     "gpioStatusChangeRequest",
                     "parseGpioStatusFunction"]
    },
    "form": [
        "gpioList",
        "requestTimeout",
        {
            "key": "switchPanelBackgroundColor",
            "type": "color"
        },
        {
            "key": "gpioStatusRequest",
            "items": [
                "gpioStatusRequest.method",
                {
                    "key": "gpioStatusRequest.paramsBody",
                    "type": "json"
                }
            ]
        },
        {
            "key": "gpioStatusChangeRequest",
            "items": [
                "gpioStatusChangeRequest.method",
                {
                    "key": "gpioStatusChangeRequest.paramsBody",
                    "type": "json"
                }
            ]
        },
        {
            "key": "parseGpioStatusFunction",
            "type": "javascript"
        }
    ]
}
