    settings.falseValue = utils.defaultValue(utils.customTranslation(settings.falseValue, settings.falseValue), false);

    map = {
        true: settings.trueValue,
        false: settings.falseValue
    };

    $scope.checkboxValue = false;
    $scope.currentValue = map[$scope.checkboxValue];

    $scope.attributeUpdateFormGroup = $scope.fb.group({checkboxValue: [$scope.checkboxValue]});

    $scope.changed = function() {
        $scope.checkboxValue = $scope.attributeUpdateFormGroup.get('checkboxValue').value;
        $scope.currentValue = map[$scope.checkboxValue];
        $scope.updateAttribute();
    };

