self.onInit = function() {
    self.ctx.map = new TbMapWidgetV2('google-map', false, self.ctx);
}

self.onDataUpdated = function() {
    self.ctx.map.update();
}

self.onResize = function() {
    self.ctx.map.resize();
}

self.getSettingsSchema = function() {
    return TbMapWidgetV2.settingsSchema('google-map');
}

self.getDataKeySettingsSchema = function() {
    return TbMapWidgetV2.dataKeySettingsSchema('google-map');
}

self.onDestroy = function() {
}

