descriptor.controllerScript:
self.onInit = function() {
    $scope = self.ctx.$scope;
    utils = $scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};
    settings.showTooltip = utils.defaultValue(settings.showTooltip, true);

    Chart.defaults.global.tooltips.enabled = settings.showTooltip;

    var barData = {
        labels: [],
        datasets: []
    };

    for (var i = 0; i < self.ctx.datasources.length; i++) {
        var datasource = self.ctx.datasources[i];
        for (var d = 0; d < datasource.dataKeys.length; d++) {
            var dataKey = datasource.dataKeys[d];
            var units = dataKey.units && dataKey.units.length ? dataKey.units : self.ctx.units;
            units = units ? (' (' + units + ')') : '';
            var dataset = {
                label: dataKey.label + units,
                data: [0],
                backgroundColor: [dataKey.color],
                borderColor: [dataKey.color],
                borderWidth: 1
            }
            barData.datasets.push(dataset);
        }
    }

    var ctx = $('#barChart', self.ctx.$container);
    self.ctx.chart = new Chart(ctx, {
        type: 'bar',
        data: barData,
        options: {
            responsive: false,
            maintainAspectRatio: false,
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero:true
                    }
                }]
            }
        }
    });

    self.onResize();
}

self.onDataUpdated = function() {
    var c = 0;
    for (var i = 0; i < self.ctx.chart.data.datasets.length; i++) {
        var dataset = self.ctx.chart.data.datasets[i];
        var cellData = self.ctx.data[i]; 
        if (cellData.data.length > 0) {
            var decimals;
            if (typeof cellData.dataKey.decimals !== 'undefined' 
                && cellData.dataKey.decimals !== null ) {
                decimals = cellData.dataKey.decimals; 
            } else {
                decimals = self.ctx.decimals;
            }
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = self.ctx.utils.formatValue(tvPair[1], decimals);
            dataset.data[0] = parseFloat(value);
        }
    }
    self.ctx.chart.update();
}

self.onResize = function() {
    self.ctx.chart.resize();
}

self.onDestroy = function() {
    self.ctx.chart.destroy();
    self.ctx.chart = null;
}

