descriptor.templateCss:
.claim-form {
    overflow: hidden;
    height: 100%;
    display: flex;
    flex-direction: column;
}

descriptor.controllerScript:
let $scope;

self.onInit = function() {
    self.ctx.ngZone.run(function() {
       init(); 
       self.ctx.detectChanges(true);
    });
}

function init() {
    $scope = self.ctx.$scope;
    let $injector = $scope.$injector;
    let utils = $scope.$injector.get(self.ctx.servicesMap.get('utils'));
    let $translate = $scope.$injector.get(self.ctx.servicesMap.get('translate'));
    let deviceService = $scope.$injector.get(self.ctx.servicesMap.get('deviceService'));
    let settings = self.ctx.settings || {};

    $scope.toastTargetId = 'device-claiming-widget' + utils.guid();
    $scope.secretKeyField = settings.deviceSecret;
    $scope.showLabel = settings.showLabel;

    let titleTemplate = "";
    let successfulClaim = utils.customTranslation(settings.successfulClaimDevice, settings.successfulClaimDevice) || $translate.instant('widgets.input-widgets.claim-successful');
    let failedClaimDevice = utils.customTranslation(settings.failedClaimDevice, settings.failedClaimDevice) || $translate.instant('widgets.input-widgets.claim-failed');
    let deviceNotFound = utils.customTranslation(settings.deviceNotFound, settings.deviceNotFound) || $translate.instant('widgets.input-widgets.claim-not-found');

    if (settings.widgetTitle && settings.widgetTitle.length) {
        titleTemplate = utils.customTranslation(settings.widgetTitle, settings.widgetTitle);
    } else {
        titleTemplate = self.ctx.widgetConfig.title;
    }
    self.ctx.widgetTitle = titleTemplate;

    $scope.deviceLabel = utils.customTranslation(settings.deviceLabel, settings.deviceLabel) || $translate.instant('widgets.input-widgets.device-name');
    $scope.requiredErrorDevice= utils.customTranslation(settings.requiredErrorDevice, settings.requiredErrorDevice) || $translate.instant('widgets.input-widgets.device-name-required');

    $scope.secretKeyLabel = utils.customTranslation(settings.secretKeyLabel, settings.secretKeyLabel) || $translate.instant('widgets.input-widgets.secret-key');
    $scope.requiredErrorSecretKey= utils.customTranslation(settings.requiredErrorSecretKey, settings.requiredErrorSecretKey) || $translate.instant('widgets.input-widgets.secret-key-required');

    $scope.labelClaimButon = utils.customTranslation(settings.labelClaimButon, settings.labelClaimButon) || $translate.instant('widgets.input-widgets.claim-device');

    $scope.claimDeviceFormGroup = $scope.fb.group(
        {deviceName: ['', [$scope.validators.required]]}
    );
    if ($scope.secretKeyField) {
        $scope.claimDeviceFormGroup.addControl('deviceSecret', $scope.fb.control('', [$scope.validators.required]));
    }

    $scope.claim = function(claimDeviceForm) {
        $scope.loading = true;

        let deviceName = $scope.claimDeviceFormGroup.get('deviceName').value;
        let claimRequest = {};
        if ($scope.secretKeyField) {
            claimRequest.secretKey = $scope.claimDeviceFormGroup.get('deviceSecret').value;
        }
        deviceService.claimDevice(deviceName, claimRequest, { ignoreErrors: true }).subscribe(
            function (data) {
                successClaim(claimDeviceForm);
                self.ctx.detectChanges();
            },
            function (error) {
                $scope.loading = false;
                if(error.status == 404) {
                    $scope.showErrorToast(deviceNotFound, 'bottom', 'left', $scope.toastTargetId);
                } else {
                    let errorMessage = failedClaimDevice;
                    if (error.status !== 400) {
                        if (error.error && error.error.message) {
                            errorMessage = error.error.message;
                        }
                    }
                    $scope.showErrorToast(errorMessage, 'bottom', 'left', $scope.toastTargetId);
                }    
                self.ctx.detectChanges();
            }
        );
    }

    function successClaim(claimDeviceForm) {
        let deviceObj = {
            deviceName: ''
        };
        if ($scope.secretKeyField) {
            deviceObj.deviceSecret = '';
        }    
        claimDeviceForm.resetForm();    
        $scope.claimDeviceFormGroup.reset(deviceObj);
        $scope.loading = false;
        $scope.showSuccessToast(successfulClaim, 2000, 'bottom', 'left', $scope.toastTargetId);
        self.ctx.updateAliases();
    }

}

