descriptor.controllerScript:
self.onInit = function() {
    if (self.ctx.datasources && self.ctx.datasources.length) {
        self.ctx.$scope.entityId = self.ctx.datasources[0].entity.id;
        self.ctx.$scope.defaultTab = self.ctx.stateController?.getStateParams()?.defaultTab;
    }
};

self.typeParameters  = function() {
    return {
        dataKeysOptional: true,
        singleEntity: true
    };
}

