descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.valueCardWidget.onInit();
};

self.onDataUpdated = function() {
    self.ctx.$scope.valueCardWidget.onDataUpdated();
};

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true,
        horizontal: true,
        previewWidth: '420px',
        previewHeight: '90px',
        embedTitlePanel: true,
        supportsUnitConversion: true,
        defaultDataKeysFunction: function() {
            return [{ name: 'radon', label: 'Radon level', type: 'timeseries' }];
        }
    };
};

self.onDestroy = function() {
};

