self.onInit = function() {
    self.ctx.varsRegex = /\$\{([^\}]*)\}/g;
    self.ctx.htmlSet = false;

    var cssParser = new cssjs();
    cssParser.testMode = false;
    var namespace = 'html-value-card-' + hashCode(self.ctx.settings.cardCss);
    cssParser.cssPreviewNamespace = namespace;
    cssParser.createStyleElement(namespace, self.ctx.settings.cardCss);
    self.ctx.$container.addClass(namespace);
    var evtFnPrefix = 'htmlValueCard_' + Math.abs(hashCode(self.ctx.settings.cardCss + self.ctx.settings.cardHtml + self.ctx.widget.id));
    self.ctx.html = '<div style="height:100%" onclick="' + evtFnPrefix + '_onClickFn(event)">' + 
                    self.ctx.settings.cardHtml + 
                    '</div>';

    self.ctx.replaceInfo = processHtmlPattern(self.ctx.html, self.ctx.data);

    updateHtml();

    window[evtFnPrefix + '_onClickFn'] = function (event) {
        self.ctx.actionsApi.elementClick(event);
    }

    function hashCode(str) {
        var hash = 0;
        var i, char;
        if (str.length === 0) return hash;
        for (i = 0; i < str.length; i++) {
            char = str.charCodeAt(i);
            hash = ((hash << 5) - hash) + char;
            hash = hash & hash;
        }
        return hash;
    }

    function processHtmlPattern(pattern, data) {
        var match = self.ctx.varsRegex.exec(pattern);
        var replaceInfo = {};
        replaceInfo.variables = [];
        while (match !== null) {
            var variableInfo = {};
            variableInfo.dataKeyIndex = -1;
            var variable = match[0];
            var label = match[1];
            var valDec = 2;
            var splitVals = label.split(':');
            if (splitVals.length > 1) {
                label = splitVals[0];
                valDec = parseFloat(splitVals[1]);
            }
            variableInfo.variable = variable;
            variableInfo.valDec = valDec;
            if (label == 'entityName') {
                variableInfo.isEntityName = true;
            } else if (label == 'entityLabel') {
                variableInfo.isEntityLabel = true;
            } else if (label.startsWith('#')) {
                var keyIndexStr = label.substring(1);
                var n = Math.floor(Number(keyIndexStr));
                if (String(n) === keyIndexStr && n >= 0) {
                    variableInfo.dataKeyIndex = n;
                }
            }
            if (!variableInfo.isEntityName && !variableInfo.isEntityLabel && variableInfo.dataKeyIndex === -1) {
                for (var i = 0; i < data.length; i++) {
                     var datasourceData = data[i];
                     var dataKey = datasourceData.dataKey;
                     if (dataKey.label === label) {
                         variableInfo.dataKeyIndex = i;
                         break;
                     }
                }
            }
            replaceInfo.variables.push(variableInfo);
            match = self.ctx.varsRegex.exec(pattern);
        }
        return replaceInfo;
    }    
}

self.onDataUpdated = function() {
    updateHtml();
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        singleEntity: true,
        dataKeysOptional: true
    };
}


self.onDestroy = function() {
}

function isNumber(n) {
    return !isNaN(parseFloat(n)) && isFinite(n);
}

function padValue(val, dec, int) {
    var i = 0;
    var s, strVal, n;

    val = parseFloat(val);
    n = (val < 0);
    val = Math.abs(val);

    if (dec > 0) {
        strVal = val.toFixed(dec).toString().split('.');
        s = int - strVal[0].length;

        for (; i < s; ++i) {
            strVal[0] = '0' + strVal[0];
        }

        strVal = (n ? '-' : '') + strVal[0] + '.' + strVal[1];
    }

    else {
        strVal = Math.round(val).toString();
        s = int - strVal.length;

        for (; i < s; ++i) {
            strVal = '0' + strVal;
        }

        strVal = (n ? '-' : '') + strVal;
    }

    return strVal;
}

function updateHtml() {
    var $injector = self.ctx.$scope.$injector;
    var utils = $injector.get(self.ctx.servicesMap.get('utils'));
    var text = self.ctx.html;
    var updated = false;
    for (var v in self.ctx.replaceInfo.variables) {
        var variableInfo = self.ctx.replaceInfo.variables[v];
        var txtVal = '';
        if (variableInfo.dataKeyIndex > -1) {
            var varData = self.ctx.data[variableInfo.dataKeyIndex].data;
            if (varData.length > 0) {
                var val = varData[varData.length-1][1];
                if (isNumber(val)) {
                    txtVal = padValue(val, variableInfo.valDec, 0);
                } else {
                    txtVal = val;
                }
            }
        } else if (variableInfo.isEntityName) {
            if (self.ctx.defaultSubscription.datasources.length) {
                txtVal = self.ctx.defaultSubscription.datasources[0].entityName;
            } else {
                txtVal = 'Unknown';
            }
        } else if (variableInfo.isEntityLabel) {
            if (self.ctx.defaultSubscription.datasources.length) {
                txtVal = self.ctx.defaultSubscription.datasources[0].entityLabel || self.ctx.defaultSubscription.datasources[0].entityName;
            } else {
                txtVal = 'Unknown';
            }
        }
        if (typeof variableInfo.lastVal === undefined ||
            variableInfo.lastVal !== txtVal) {
            updated = true;
            variableInfo.lastVal = txtVal;
        }
        text = text.split(variableInfo.variable).join(txtVal);
    }
    if (updated || !self.ctx.htmlSet) {
        text = replaceCustomTranslations(text);
        self.ctx.$container.html(text);
        if (!self.ctx.htmlSet) {
            self.ctx.htmlSet = true;
        }
    }

    function replaceCustomTranslations (pattern) {
        var customTranslationRegex = new RegExp('{i18n:[^{}]+}', 'g');
        pattern = pattern.replace(customTranslationRegex, getTranslationText);
        return pattern;
    }

    function getTranslationText (variable) {
        return utils.customTranslation(variable, variable);

    }
}

