self.onInit = function() {
    $scope = self.ctx.$scope;
    utils = $scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};
    settings.showTooltip = utils.defaultValue(settings.showTooltip, true);

    Chart.defaults.global.tooltips.enabled = settings.showTooltip;

    var barData = {
        labels: [],
        datasets: []
    };

    var backgroundColor = tinycolor(self.ctx.data[0].dataKey.color);
    backgroundColor.setAlpha(0.2);
    var borderColor = tinycolor(self.ctx.data[0].dataKey.color);
    borderColor.setAlpha(1);
    var dataset = {
        label: self.ctx.datasources[0].name,
        data: [],
        backgroundColor: backgroundColor.toRgbString(),
        borderColor: borderColor.toRgbString(),
        pointBackgroundColor: borderColor.toRgbString(),
        pointBorderColor: borderColor.darken().toRgbString(),
        borderWidth: 1
    }

    barData.datasets.push(dataset);

    for (var i = 0; i < self.ctx.data.length; i++) {
        var dataKey = self.ctx.data[i].dataKey;
        var units = dataKey.units && dataKey.units.length ? dataKey.units : self.ctx.units;
        units = units ? (' (' + units + ')') : '';
        barData.labels.push(dataKey.label + units);
        dataset.data.push(0);
    }

    var floatingPoint;
    if (typeof self.ctx.decimals !== 'undefined' && self.ctx.decimals !== null) {
        floatingPoint = self.ctx.widget.config.decimals;
    } else {
        floatingPoint = 2;
    }

    var ctx = $('#radarChart', self.ctx.$container);
    self.ctx.chart = new Chart(ctx, {
        type: 'radar',
        data: barData,
        options: {
            responsive: false,
            maintainAspectRatio: false,
            scale: {
                ticks: {
                    callback: function(tick) {
                    	return tick.toFixed(floatingPoint);
                    }
                }
            }
        }
    });

    self.onResize();
}

self.onDataUpdated = function() {
    for (var i = 0; i < self.ctx.data.length; i++) {
        var cellData = self.ctx.data[i];
        if (cellData.data.length > 0) {
            var decimals;
            if (typeof cellData.dataKey.decimals !== 'undefined' 
                && cellData.dataKey.decimals !== null ) {
                decimals = cellData.dataKey.decimals; 
            } else {
                decimals = self.ctx.decimals;
            }
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = self.ctx.utils.formatValue(tvPair[1], decimals);
            self.ctx.chart.data.datasets[0].data[i] = parseFloat(value);
        }
    }    
    self.ctx.chart.update();
}

self.onResize = function() {
    if (self.ctx.height >= 70) {
        self.ctx.chart.resize();
    }
}

self.onDestroy = function() {
    self.ctx.chart.destroy();
    self.ctx.chart = null;
}

