descriptor.templateCss:
.tb-rpc-button {
    width: 100%;
    height: 100%;
}

.tb-rpc-button .title-container {
    font-weight: 500;
    white-space: nowrap;
    margin: 10px 0;
}

.tb-rpc-button .button-container div{
    min-width: 80%
}

.tb-rpc-button .button-container .mat-mdc-button{
    width: 100%;
    margin: 0;
}

.tb-rpc-button .error-container {
    position: absolute;
    top: 2%;
    right: 0;
    left: 0;
    z-index: 4;
    height: 14px;
}

.tb-rpc-button .error-container .button-error {
    color: #ff3315;
    white-space: nowrap;
}

descriptor.controllerScript:
var requestPersistent = false;
var persistentPollingInterval = 5000;

self.onInit = function() {
    if (self.ctx.settings.requestPersistent) {
        requestPersistent = self.ctx.settings.requestPersistent;
    }
    if (self.ctx.settings.persistentPollingInterval) {
        persistentPollingInterval = self.ctx.settings.persistentPollingInterval;
    }

    self.ctx.ngZone.run(function() {
       init(); 
       self.ctx.detectChanges();
    });
};

function init() {
    let rpcEnabled = self.ctx.defaultSubscription.rpcEnabled;

    self.ctx.$scope.buttonLable = self.ctx.settings.buttonText;
    self.ctx.$scope.showTitle = self.ctx.settings.title &&
        self.ctx.settings.title.length ? true : false;
    self.ctx.$scope.title = self.ctx.settings.title;
    self.ctx.$scope.styleButton = self.ctx.settings.styleButton;

    if (self.ctx.settings.styleButton.isPrimary ===
        false) {
        self.ctx.$scope.customStyle = {
            'background-color': self.ctx.$scope.styleButton.bgColor,
            'color': self.ctx.$scope.styleButton.textColor
        };
    }

    if (!rpcEnabled) {
        self.ctx.$scope.error =
            'Target device is not set!';
    }

    self.ctx.$scope.sendCommand = function() {
        var rpcMethod = self.ctx.settings.methodName;
        var rpcParams = self.ctx.settings.methodParams;
        if (rpcParams.length) {
            try {
                rpcParams = JSON.parse(rpcParams);
            } catch (e) {}
        }
        var timeout = self.ctx.settings.requestTimeout;
        var oneWayElseTwoWay = self.ctx.settings.oneWayElseTwoWay ?
            true : false;

        var commandPromise;
        if (oneWayElseTwoWay) {
            commandPromise = self.ctx.controlApi.sendOneWayCommand(
                rpcMethod, rpcParams, timeout, requestPersistent, persistentPollingInterval);
        } else {
            commandPromise = self.ctx.controlApi.sendTwoWayCommand(
                rpcMethod, rpcParams, timeout, requestPersistent, persistentPollingInterval);
        }
        commandPromise.subscribe(
            function success() {
                self.ctx.$scope.error = "";
                self.ctx.detectChanges();
            },
            function fail(rejection) {
                if (self.ctx.settings.showError) {
                    self.ctx.$scope.error =
                        rejection.status + ": " +
                        rejection.statusText;
                    self.ctx.detectChanges();
                }
            }
        );
    };
}

self.onDestroy = function() {
    self.ctx.controlApi.completedCommand();
}
