descriptor.templateCss:
.error {
    font-size: 14px !important;
    color: maroon;/*rgb(250,250,250);*/
    background-color: transparent;
    padding: 6px;
}

.error span {
    margin: auto;
}

.gpio-panel {
    padding-top: 10px;
    white-space: nowrap;
}

.gpio-panel section.flex-1 {
    min-width: 0px;
}


.switch-panel {
    margin: 0;
    height: 32px;
    width: 66px;
    min-width: 66px;
}

.switch-panel mat-slide-toggle {
    margin: 0;
    width: 36px;
    min-width: 36px;
}

.switch-panel.col-0 mat-slide-toggle {
    margin-left: 8px;
    margin-right: 4px;
}

.switch-panel.col-1 mat-slide-toggle {
    margin-left: 4px;
    margin-right: 8px;
}

.gpio-row {
    height: 32px;
}

.pin {
    margin-top: auto;
    margin-bottom: auto;
    color: white;
    font-size: 12px;
    width: 16px;
    min-width: 16px;
}

.switch-panel.col-0 .pin {
    margin-left: auto;
    padding-left: 2px;
    text-align: right;
}

.switch-panel.col-1 .pin {
    margin-right: auto;

    text-align: left;
}

.gpio-left-label {
    margin-right: 8px;
}

.gpio-right-label {
    margin-left: 8px;
}
