descriptor.templateCss:
.legend {
    font-size: 13px;
    line-height: 10px;
}

.legend table { 
    border-spacing: 0px;
    border-collapse: separate;
}

.mouse-events .flot-overlay {
    cursor: crosshair; 
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.valueChartCardWidget.onInit();
};

self.onDataUpdated = function() {
    self.ctx.$scope.valueChartCardWidget.onDataUpdated();
};

self.onLatestDataUpdated = function() {
    self.ctx.$scope.valueChartCardWidget.onLatestDataUpdated();
}

self.onEditModeChanged = function() {
    self.ctx.$scope.valueChartCardWidget.onEditModeChanged();
}

self.onDestroy = function() {
    self.ctx.$scope.valueChartCardWidget.onDestroy();
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true,
        previewWidth: '300px',
        previewHeight: '150px',
        embedTitlePanel: true,
        supportsUnitConversion: true,
        hasAdditionalLatestDataKeys: true,
        defaultDataKeysFunction: function() {
            return [
                 { name: 'co2', label: 'CO2 level', type: 'timeseries', color: 'rgba(0, 0, 0, 0.87)'}
            ];
        },
        defaultLatestDataKeysFunction: function(configComponent) {
            return [{ name: 'co2', label: 'Latest', type: 'timeseries'}];
        }
    };
}

