descriptor.templateCss:
.legend {
    font-size: 13px;
    line-height: 10px;
}

.legend table { 
    border-spacing: 0px;
    border-collapse: separate;
}

.mouse-events .flot-overlay {
    cursor: crosshair; 
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onInit();
};

self.onDataUpdated = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onDataUpdated();
};

self.onLatestDataUpdated = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onLatestDataUpdated();
}

self.onResize = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onResize();
}

self.onEditModeChanged = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onEditModeChanged();
}

self.onDestroy = function() {
    self.ctx.$scope.aggregatedValueCardWidget.onDestroy();
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1,
        singleEntity: true,
        previewWidth: '400px',
        previewHeight: '300px',
        embedTitlePanel: true,
        hasAdditionalLatestDataKeys: true,
        defaultDataKeysFunction: function() {
            return [
                 { name: 'temperature', label: 'Temperature', type: 'timeseries', color: 'rgba(0, 0, 0, 0.87)', units: '°C', decimals: 0 }
            ];
        },
        defaultLatestDataKeysFunction: function(configComponent, configData) {
            return configComponent.createDefaultAggregatedValueLatestDataKeys(configData, 'temperature', '°C', 0);
        }
    };
}

