descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.windSpeedDirectionWidget.onInit();
};

self.onDataUpdated = function() {
    self.ctx.$scope.windSpeedDirectionWidget.onDataUpdated();
};

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 2,
        singleEntity: true,
        previewWidth: '270px',
        previewHeight: '270px',
        embedTitlePanel: true,
        supportsUnitConversion: true,
        defaultDataKeysFunction: function() {
            return [{ name: 'direction', label: 'Wind Direction', type: 'timeseries' },
                    { name: 'speed', label: 'Wind Speed', type: 'timeseries',
                      units: 'm/s', decimals: 1 }];
        }
    };
};

self.actionSources = function() {
    return {
        'cardClick': {
            name: 'widget-action.card-click',
            multiple: false
        }
    };
}

self.onDestroy = function() {
};

