let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let userSettingsService = $injector.get(widgetContext.servicesMap.get('userSettingsService'));

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;
    let userSettings;
    userSettingsService.loadUserSettings().subscribe(settings=> {
        userSettings = settings;
        if (!userSettings.createdGatewaysCount) userSettings.createdGatewaysCount = 0;
    });
    

    vm.addEntityFormGroup = vm.fb.group({
     entityName: ['', [vm.validators.required]],
     entityType: ['DEVICE'],
     entityLabel: [''],
     type: ['', [vm.validators.required]],
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };


    vm.save = function($event) {
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (device) {
                widgetContext.updateAliases();
                userSettingsService.putUserSettings({ createdGatewaysCount: ++userSettings.createdGatewaysCount }).subscribe(_=>{
                });
                vm.dialogRef.close(null);
                openCommandDialog(device, $event);
            }
        );
    };
    
    function openCommandDialog(device, $event) {
        vm.device = device;
        let openCommandAction = widgetContext.actionsApi.getActionDescriptors("actionCellButton").find(action => action.name == "Launch command");
        widgetContext.actionsApi.handleWidgetAction($event, openCommandAction, device.id, device.name, {newDevice: true});
        goToConfigState();
    }

    
    function goToConfigState() {
        const stateParams = {};
        stateParams.entityId = vm.device.id;
        stateParams.entityName = vm.device.name;
        const newStateParams = {
            targetEntityParamName: 'default',
            new_gateway: {
                entityId: vm.device.id,
                entityName: vm.device.name
            }
        }
        const params = {...stateParams, ...newStateParams};
        widgetContext.stateController.openState('gateway_details', params, false);
    }

    function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entity = {
            name: formValues.entityName,
            type: formValues.type,
            label: formValues.entityLabel,
            additionalInfo: {
                gateway: true
            }
        };
        return deviceService.saveDevice(entity);
    }
}

