package org.thingsboard.server.common.data;

import com.fasterxml.jackson.annotation.JsonGetter;
import com.fasterxml.jackson.annotation.JsonIgnore;
import com.fasterxml.jackson.annotation.JsonSetter;
import io.swagger.v3.oas.annotations.media.Schema;
import lombok.Data;
import lombok.EqualsAndHashCode;
import lombok.extern.slf4j.Slf4j;
import org.thingsboard.server.common.data.id.TbResourceId;

import java.io.Serial;
import java.util.Base64;
import java.util.Optional;

@Slf4j
@Data
@EqualsAndHashCode(callSuper = true)
public class TbResource extends TbResourceInfo {

    @Serial
    private static final long serialVersionUID = 7379609705527272306L;

    private byte[] data;

    private byte[] preview;

    public TbResource() {
        super();
    }

    public TbResource(TbResourceId id) {
        super(id);
    }

    public TbResource(TbResourceInfo resourceInfo) {
        super(resourceInfo);
    }

    public TbResource(TbResource resource) {
        super(resource);
        this.data = resource.data;
        this.preview = resource.preview;
    }

    @Schema(description = "Resource data.", example = "", accessMode = Schema.AccessMode.READ_WRITE)
    @JsonGetter("data")
    public String getEncodedData() {
        return Optional.ofNullable(data)
                .map(Base64.getEncoder()::encodeToString)
                .orElse(null);
    }

    @JsonSetter("data")
    public void setEncodedData(String data) {
        this.data = Optional.ofNullable(data)
                .map(Base64.getDecoder()::decode)
                .orElse(null);
    }

    @JsonGetter("preview")
    public String getEncodedPreview() {
        return Optional.ofNullable(preview)
                .map(Base64.getEncoder()::encodeToString)
                .orElse(null);
    }

    @JsonSetter("preview")
    public void setEncodedPreview(String preview) {
        this.preview = Optional.ofNullable(preview)
                .map(Base64.getDecoder()::decode)
                .orElse(null);
    }

    @JsonIgnore
    public TbResourceDataInfo toResourceDataInfo() {
        return new TbResourceDataInfo(data, getDescriptor());
    }

    @Override
    public String toString() {
        return super.toString();
    }

}
