.feature {
    position: relative;
    cursor: default;
    font-weight: 400;
    font-size: 14px;
    line-height: 28px;
    letter-spacing: 0.2px;
    color: rgba(0, 0, 0, 0.76);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.feature:before {
    content: "check";
    font-family: 'Material Icons Round';
    display: inline-block;
    font-size: 20px;
    line-height: 20px;
    color: #198038;
    font-weight: 600;
    position: relative;
    background: #F3F6FA;
    border-radius: 4px;
    width: 20px;
    height: 20px;
    padding: 2px;
    vertical-align: sub;
    margin-right: 8px;
}

.feature.pe:before {
    content: "close";
    background: #F4F4F4;
    color: rgba(0, 0, 0, 0.12);
}

.feature.pe:after {
    position: relative;
    display: inline-block;
    margin-left: 4px;
    vertical-align: baseline;
    top: -0.5em;
    content: "PE";
    color: #305680;
    font-weight: 700;
    font-size: 12px;
    line-height: 12px;
    letter-spacing: 0.0015em;
}

.feature+.feature {
    margin-top: 4px;
}

@media screen and (min-width: 960px) and (max-width: 1819px) {
    .tb-switch-to-pe {
        height: 24px;
        font-size: 12px;
        padding: 0 4px;
    }

    .feature+.feature {
        margin-top: 2px;
    }

    .feature {
        font-size: 11px;
        line-height: 18px;
    }

    .feature:before {
      font-size: 14px;
      line-height: 14px;
      width: 14px;
      height: 14px;
      margin-right: 4px;
    }
    .feature.pe:after {
        margin-left: 2px;
        top: -0.5em;
        content: "PE";
        font-size: 8px;
        line-height: 8px;
    }

}

