settings.markdownTextFunction:
let html = '<div style="width: 100%; height: 100%;">';
let supportMobile = '${mobileQrEnabled:valuePlaceholder}';
let supportMobileValue = supportMobile.match(/\${mobileQrEnabled:([^}]+)}/)[1];
if (supportMobileValue === 'true') {
    ctx.dashboardWidget.widgetLayout.mobileHide = false;
    html += '<tb-dashboard-state stateId="mobile_app_qr_code" [ctx]="ctx"></tb-dashboard-state>';
} else {
    ctx.dashboardWidget.widgetLayout.mobileHide = true;
    html += '<tb-getting-started-widget style="padding: 16px;"></tb-getting-started-widget>';
}
html += '</div>';
return html;

