settings.markdownCss:
.tb-card-content {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.tb-card-content {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: row;
}

.tb-content-container {
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    gap: 12px;
}

.tb-card-header {
    height: 36px;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}

.tb-item-cards {
    flex: 1;
    display: flex;
    flex-direction: row;
    gap: 12px;
    overflow: hidden;
}

a.tb-item-card {
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 8px 12px;
    border: 1px solid;
    border-radius: 10px;
    margin-bottom: 12px;
    overflow: hidden;
    justify-content: space-evenly;
}

a.tb-item-card.tb-inactive {
    background: rgba(209, 39, 48, 0.04);
    border-color: rgba(209, 39, 48, 0.06);
}

a.tb-item-card.tb-active {
    background: rgba(48, 86, 128, 0.04);
    border-color: rgba(48, 86, 128, 0.12);
}

a.tb-item-card.tb-total {
    background: rgba(0, 0, 0, 0.01);
    border-color: rgba(0, 0, 0, 0.05);
}

.tb-item-title-container {
    display: grid;
}

.tb-item-title {
    font-weight: 400;
    font-size: 14px;
    line-height: 20px;
    letter-spacing: 0.2px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;    
    color: rgba(0, 0, 0, 0.76);
}

.tb-item-title.tb-home-widget-link:after {
    position: absolute;
    right: 0;
}

a.tb-item-card:hover .tb-item-title.tb-home-widget-link:after { 
    color: rgba(0, 0, 0, 0.38);
}

a.tb-item-card:hover {
    box-shadow: 0px 4px 10px rgba(23, 33, 90, 0.08);
}

.tb-count-container {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: center;
}

.tb-count {
    font-style: normal;
    font-weight: 500;
    font-size: 24px;
    line-height: 36px;
    white-space: nowrap;
    color: rgba(0, 0, 0, 0.87);
}

@media screen and (max-width: 959px) {
    .tb-item-cards {
        flex-direction: column;
    }
    a.tb-item-card {
        margin-bottom: 0;
    }
}

@media screen and (max-width: 1279px) {
    a.tb-item-card {
        flex-direction: row;
        align-items: center;
    }
    .tb-item-title.tb-home-widget-link:after {
        position: relative;
    }
    .tb-count-container {
        align-items: flex-end;
    }
}

@media screen and (min-width: 960px) and (max-width: 1819px) {
    .tb-item-title {
        font-size: 11px;
        line-height: 16px;
    }
    .tb-count {
        font-size: 16px;
        line-height: 24px;
    }
    a.tb-item-card {
        padding: 4px 8px;
        margin-bottom: 6px;
    }
    a.tb-item-card:hover {
        box-shadow: 0px 2px 5px rgba(23, 33, 90, 0.08);
    }
}

